<?php	class RegistorAction extends Action{		public function index(){			$this->display();		}		public function usernameajax(){			$user=M('user');			$res=$user->where(array("username"=>"{$_POST['username']}"))->select();			if($res>0){				echo 1;			}else{				echo 0;			}			//dump($_POST);		}				public function doregistor(){					if(md5($_POST['Code'])!=$_SESSION['verify']){				$this->error("验证码错误");			}			$m=M('user');			$_POST['password']=md5($_POST['password']);			$_POST['reg_time']=time();			$res=$m->add($_POST);			if($res){				$_SESSION['user']['username']=$_POST['username'];				$_SESSION['user']['id']=$res;				//$this->success("注册成功","__APP__/Index/index");				$this->redirect('Index/index');			}else{				$this->error("注册用户失败");			}		}			}